/**
 * 绑定响应式对象
 * @param fn 将响应式对象写在 fn 内，该对象重新赋值时会自行触发 fn()
 * 当返回 true 时，该函数将在依赖收集中删除，避免占用过多的内存
 */
export declare function binding(fn: Function): void;
/**
 * 依赖收集
 * @param key 存入 funcsMap 的键
 */
export declare function dependencyCollection(key: object): void;
/**
 * 派发更新
 * @param key 存入 funcsMap 的键
 */
export declare function distributeUpdates(key: object): void;
/**
 * 回收依赖，清空收集的依赖
 * @param key ref 或 reactive 对象
 */
export declare function recycleDepend(key: object): void;
