import { AnyObj } from "../utils";
/**
 * 将数据变为响应式数据（深度）
 * @param target
 * @returns
*/
export declare function reactive<T extends AnyObj>(target: T): T;
/**
 * 判断是否为 reactive 对象
 * @param reactive
 * @returns
 */
export declare function isReactive<T extends AnyObj>(reactive: T): boolean;
/**
 * reactive 对象转普通对象
 * @param reactive
 * @returns
 */
export declare function toRaw<T extends AnyObj>(reactive: T): T;
/**
 * 检测是否为响应式对象
 * @param proxy
 */
export declare function isProxy<T extends AnyObj>(proxy: T): boolean;
/**
 * 标记该对象将不能设置为代理对象
 * @param obj
 * @returns
 */
export declare function markRaw<T extends AnyObj>(obj: T): T;
/**
 * reactive 的浅层作用形式
 * @param obj
 * @returns
 */
export declare function shallowReactive<T extends AnyObj>(obj: T): any;
